/* 
 * ===========================================================
 * MAIN SCRIPT- OSCAR THEMES
 * ===========================================================
 * This script manage all the js functions and the 3r party plugins.
 * 
*/
(function($) {

  "use strict";

  $(document).on("ready",function(){

    /*=========================== PAGE LOADER SCRIPT */
    $(window).on('load',function(){
      $("body").imagesLoaded(function(){
        $(".loader-cont").fadeOut();
        $("#loader-overflow").delay(200).fadeOut(700);
      });
    });
    /*=========================== MOBILE NAVIGATION SCRIPT */
    if($('.main-menu nav').length){   
      $('.main-menu nav').meanmenu({
        meanMenuContainer: '.mobile-menu',
        meanScreenWidth: "991"
      });
    }
    if ($('#opg-nav').length) {
      $('#opg-nav').onePageNav({
        currentClass: 'active',
        changeHash: false,
        scrollSpeed: 750 
      });
    }
    if ($('.sticky-header').length) {
      if (window.matchMedia("(min-width: 768px)").matches) {
        $(function() {
            var header = $(".sticky-header");
            $(".sticky-header").parent().height( $("header").height() );
            $(window).scroll(function() {
                var scroll = $(window).scrollTop();
                if (scroll >= 100) {
                    header.removeClass('affix-top').addClass("affix");
                } else {
                    header.removeClass("affix").addClass('affix-top');
                }
            });
        });
      }
    }

    if($('.countdown').length){
        $('.countdown').downCount({ date: '12/12/2018 12:00:00', offset: +1 });
    }
    /*=========================== FAKE FOOTER SCRIPT */
    // $(".fake-footer-inner").parent().height( $(".fake-footer-inner").height() );
    // $(".fake-footer").siblings(".main-wrapper").css({"background-color": "#fff", "z-index": "1"});
    /*=========================== ACCORDIAN SCRIPT */
      if($('.accordion-1').length){
        //custom animation for open/close
        $.fn.slideFadeToggle = function(speed, easing, callback) {
          return this.animate({opacity: 'toggle',padding: 'toggle', height: 'toggle'}, speed, easing, callback);
        };
        $('.accordion-1').accordion({
          defaultOpen: '',
          cookieName: 'nav',
          speed: 'slow',
          animateOpen: function (elem, opts) { //replace the standard slideUp with custom function
            elem.next().stop(true, true).slideFadeToggle(opts.speed);
          },
          animateClose: function (elem, opts) { //replace the standard slideDown with custom function
            elem.next().stop(true, true).slideFadeToggle(opts.speed);
          }
        });
      }
    /*=========================== COUNTER SCRIPT */
      if ($('.count-number').length) {
        $('.count-number').counterUp({
            delay: 10,
            time: 1000
        });
      }
    /*
    ============================================================
      All Slick Slider Javascript
    ============================================================
    */
      /*=========================== BLOG SLIDER START */
      // if ($('.grad-testimonial').length) {
      //   $('.grad-testimonial').slick({
      //     slidesToShow: 2,
      //     slidesToScroll: 1,
      //     arrows: false,
      //     dots: true,
      //     draggable:false,
      //     centerMode: false,
      //     vertical: true
      //   });
      // }
if ($('.grad-testimonial').length) {
      $('.grad-testimonial').slick({
        dots: true,
        infinite: true,
        speed: 300,
        slidesToShow: 2,
        vertical: true,
        adaptiveHeight: true,
        responsive: [ {
                breakpoint: 992,
                settings: {
                    slidesToScroll: 1,
                    slidesToShow: 1
                }
            }]
      });
    }
      /*=========================== TESTIMONIAL SLIDER START */
      if ($('.testimonial-thumb-slider').length) {
        $('.testimonial-thumb-slider').slick({
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: false,
          fade: true,
          asNavFor: '.testimonial-thumb-nav'
        });
      }
      if ($('.testimonial-thumb-nav').length) {
        $('.testimonial-thumb-nav').slick({
          slidesToShow: 5,
          slidesToScroll: 1,
          centerPadding:'0px',
          asNavFor: '.testimonial-thumb-slider',
          dots: false,
          arrows: false,
          centerMode: true,
          focusOnSelect: true,
          responsive: [ {
                  breakpoint: 992,
                  settings: {
                      slidesToScroll: 1,
                      slidesToShow: 3
                  }
              }, {
                  breakpoint: 638,
                  settings: {
                      slidesToShow: 3
                  }
              }, {
                  breakpoint: 481,
                  settings: {
                      slidesToShow: 1
                  }
              }]
        });
      }
      /*=========================== BLOG SLIDER START */
      if ($('.featured-slider').length) {
        $('.featured-slider').slick({
            arrows: false,
            dots: false,
            slidesToShow: 5,
            slidesToScroll: 1,
            responsive: [ {
                breakpoint: 992,
                settings: {
                    slidesToScroll: 1,
                    slidesToShow: 3
                }
            }, {
                breakpoint: 638,
                settings: {
                    slidesToShow: 2
                }
            }, {
                breakpoint: 481,
                settings: {
                    slidesToShow: 1
                }
            }]
        });
      }
      /*=========================== BLOG SLIDER START */
      if ($('.roadmap-slider').length) {
        $('.roadmap-slider').slick({
            arrows: true,
            dots: false,
            slidesToShow: 4,
            slidesToScroll: 1,
            autoplay:true,
            speed:1500,
            infinite:false,
            responsive: [ {
                breakpoint: 992,
                settings: {
                    slidesToScroll: 2,
                    slidesToShow: 2
                }
            }, {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2
                }
            },{
                breakpoint: 481,
                settings: {
                    slidesToShow: 1
                }
            }]
        });
      }
      /*=========================== BLOG SLIDER START */
      if ($('.roadmap-slider-2').length) {
        $('.roadmap-slider-2').slick({
            arrows: false,
            dots: false,
            slidesToShow: 6,
            slidesToScroll: 1,
            autoplay:true,
            speed:1500,
            infinite:false,
            responsive: [ {
                breakpoint: 992,
                settings: {
                    slidesToScroll: 1,
                    slidesToShow: 4
                }
            }, {
                breakpoint: 768,
                settings: {
                    slidesToShow: 3
                }
            },{
                breakpoint: 481,
                settings: {
                    slidesToShow: 2
                }
            }]
        });
      }
      if ($('.roadmap-slider,.roadmap-slider-2').length) {
          $('.roadmap-slider,.roadmap-slider-2').each(function(){
             // $('.container').hasClass('.roadmap-slider')
             $(this).closest('section,.section').css({"overflow": "hidden"});
          });
      }
    /*=========================== POPUP SCRIPT (MAGNIFIC POPUP) */
    if($('.popup-gallery').length) {
      $('.popup-gallery').magnificPopup({
        delegate: '.popup-icon',
        type: 'image',
        tLoading: 'Loading image #%curr%...',
        mainClass: 'mfp-3d-unfold',
        removalDelay: 500, //delay removal by X to allow out-animation
        callbacks: {
        beforeOpen: function() {
          // just a hack that adds mfp-anim class to markup 
           this.st.image.markup = this.st.image.markup.replace('mfp-figure', 'mfp-figure mfp-with-anim');
          // this.st.mainClass = this.st.el.attr('data-effect');
        }
        },
        gallery: {
          enabled: true,
          navigateByImgClick: true,
          preload: [0,1] // Will preload 0 - before current, and 1 after the current image
        },
        image: {
          tError: '<a href="%url%">The image #%curr%</a> could not be loaded.',
          /*titleSrc: function(item) {
            return item.el.attr('title') + '<small>by Marsel Van Oosten</small>';
          }*/
        }
      });
    }
    if($('.popup-youtube, .popup-vimeo, .popup-gmaps').length) {
      //VIDEO GMAPS POPUP
      $('.popup-youtube, .popup-vimeo, .popup-gmaps').magnificPopup({
        //disableOn: 700,
        type: 'iframe',
        mainClass: 'mfp-3d-unfold',
        removalDelay: 160,
        preloader: false,
        fixedContentPos: false
      });
    }
    /*=========================== NEWSLETTER SCRIPT */
    $(function () {
      'use strict';
      var $form = $('#mc-embedded-subscribe-form');
      $('#mc-embedded-subscribe').on('click', function(event) {
        if(event) event.preventDefault();
        register($form);
      });
    });
    function register($form) {
        $.ajax({
        type: $form.attr('method'),
        url: $form.attr('action'),
        data: $form.serialize(),
        cache       : false,
        dataType    : 'json',
        contentType: "application/json; charset=utf-8",
        error       : function(err) {
          $('#notification_container').html('<div id="nl-alert-container"  class="alert alert-info alert-dismissible fade in bounceIn" role="alert" ><button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>Could not connect to server. Please try again later.</div>'); 
        },
        success     : function(data) {
          if (data.result != "success") {
            var message = data.msg;
            $('#notification_container').html('<div id="nl-alert-container"  class="alert alert-info alert-dismissible fade in bounceIn" role="alert" ><button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>'+message+'</div>');
          } 

          else {
            var message = data.msg;
            $('#notification_container').html('<div id="nl-alert-container"  class="alert alert-info alert-dismissible fade in bounceIn" role="alert" ><button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>'+message+'</div>');
          }
        }
      });
    } 
  });
})(jQuery);
